import React from "react";
import {movies$ as moviesPromise} from "./movies.js";
import Filtre from "./Filtre.js";
import Filme from "./Filme.js";
import Pagination from "./Pagination.js";

export default class Cinema extends React.Component {
    constructor(props) {
       super(props);
       this.state = {
        sourceMovies: [],
        movies: [],
        page:1,
        numberByPage:5
      };
      this.updateDatePagination = this.updateDatePagination.bind(this);
    }

     componentDidMount() {
        moviesPromise.then((sourceMovies)=>{
           this.setState({ sourceMovies,movies:sourceMovies });
       });
      }

      removeElement(id) {
      const sourceMovies =   this.state.sourceMovies.filter(movie => movie.id !==id);
      const movies =   this.state.movies.filter(movie => movie.id !==id);
      this.setState({ sourceMovies,movies });
      }

      filterList(categoriesSelected) {
        const { sourceMovies } = this.state;
        const movies = sourceMovies.filter((movie)=>  categoriesSelected.indexOf(movie.category)>=0 );
        this.setState({ movies });
      }

      updateDatePagination(currentPage,numberByPage) {
     this.setState({ page:currentPage,numberByPage});
      }


    render() {
       const { sourceMovies } = this.state;
       const { movies } = this.state;
       const total=movies.length;


       const start = (this.state.page - 1) * this.state.numberByPage;
       const end =  this.state.page * this.state.numberByPage;



      const  listFilmes = movies.slice(start,end).map((filme,idx) =>  <Filme data={filme} key={idx} handleClick={ i => this.removeElement(i)}  />  );


      const categories = sourceMovies.reduce(function(accList, currentMovie) {
        if(accList.indexOf(currentMovie.category) === -1) accList.push(currentMovie.category);
        return accList;
       }, []);

      const  listCategories = categories.map((category,idx) =>  <option key={idx} value={category}>{category}</option> );
      return (
            <div className="wrapper">
            <h2>Cinéma</h2>
            <Filtre list={listCategories} submitFilter={ cat => this.filterList(cat)}  />
            <div className="flex"> {listFilmes}</div>
            <Pagination   total={total}  updateDatePagination={this.updateDatePagination}  />
            </div>
        );
    };
}
