import React from "react";
import axios from 'axios'

import Filtre from "./Filtre.js";
import Post from "./Post.js";


export default class Cinema extends React.Component {
    constructor(props) {
       super(props);
       this.state = {
        sourceData: [],
        data: [],
      };
    }

     componentDidMount() {
      axios.get('https://api.themoviedb.org/4/list/1?page=1&api_key=c34a3abefc9fd33c2fa83884832c216f', { params:{}, headers: {'Authorization': null}}).then((data) =>
      this.setState({data: data}))
      }

      removeElement(id) {
      const sourceMovies =   this.state.sourceMovies.filter(movie => movie.id !==id);
      const movies =   this.state.movies.filter(movie => movie.id !==id);
      this.setState({ sourceMovies,movies });
      }

      filterList(categoriesSelected) {
        const { sourceMovies } = this.state;
        const movies = sourceMovies.filter((movie)=>  categoriesSelected.indexOf(movie.category)>=0 );
        this.setState({ movies });
      }

      updateDatePagination(currentPage,numberByPage) {
     this.setState({ page:currentPage,numberByPage});
      }


    render() {
      const  listdata = this.state.data ? this.state.data.map((post,idx) =>  <Post data={post} key={idx} handleClick={ i => this.removeElement(i)}  />  ) : null


      const categories = this.state.sourceData.reduce(function(accList, data) {
        if(accList.indexOf(data.category) === -1) accList.push(data.category);
        return accList;
       }, []);

      const  listCategories = categories.map((category,idx) =>  <option key={idx} value={category}>{category}</option> );

      console.log('sourdeData =', this.state.sourceData)
      console.log("data =", this.state.data)

      return (
            <div className="wrapper">
            <h2>Blog</h2>
            <Filtre list={listCategories} submitFilter={ cat => this.filterList(cat)}  />
            <div className="flex"> {listdata}</div>
            </div>
        );
    };
}
