import React from "react";

export default class Filme extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            likeOrDislike: false,
        };
    }
    toggleClick(filme) {
        const currentState = this.state.likeOrDislike;       
        this.setState({ likeOrDislike: !currentState });
    }
     kFormatter(num) {
        return num > 999 ? (num/1000).toFixed(1) + 'k' : num;
    }

    render() {
        const filme = this.props.data;
        return (

            <div className="card flex-item"> 
                <div className="container">
                <h4><b>{filme.title}</b> </h4> 
                <p>{/*filme.category*/} asdsaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa </p> 
                <p>
                    <button><i className="fa fa-thumbs-up"></i> {this.kFormatter(filme.likes)}</button> 
                    <button><i className="fa fa-thumbs-down"></i> {filme.dislikes}</button>
                    <button     className="toggle-button" onClick={() => this.toggleClick(filme)}>
                    <i className={this.state.likeOrDislike ? 'fa fa-thumbs-down': 'fa fa-thumbs-up'}></i> 
                    </button>
                </p> 
                <span className="delete-movie"    onClick={() => this.props.handleClick(filme.id)}>
                <i className="fa fa-trash"></i>
                </span>
                </div>
            </div>
            );
    }

}
